class OTPResponse {
    constructor(success, errorMessage, orderId, refId) {
        this.success = success;
        this.errorMessage = errorMessage;
        this.orderId = orderId;
        this.refId = refId;
    }

    static failedRes(msg) {
        return new OTPResponse(false, msg);
    }
}

class OTPVerificationResponse {
    constructor(success, errorMessage, isOTPVerified, reason) {
        this.success = success;
        this.errorMessage = errorMessage;
        this.isOTPVerified = isOTPVerified;
        this.reason = reason;
    }

    static failedRes(msg) {
        return new OTPVerificationResponse(false, msg, false);
    }
}

module.exports = {
    OTPResponse,
    OTPVerificationResponse
};